/**
 * Initialize third-party integration tables
 */
export async function initializeThirdPartyIntegrationTables(): Promise<void> {
  try {
    if (!sql) {
      console.warn(' Database not available, skipping third-party integration table initialization');
      return;
    }

    // Integration configurations table
    await sql`
      CREATE TABLE IF NOT EXISTS integration_configs (
        id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
        name VARCHAR(255) NOT NULL,
        type VARCHAR(50) NOT NULL,
        provider VARCHAR(100) NOT NULL,
        status VARCHAR(20) DEFAULT 'inactive',
        credentials JSONB DEFAULT '{}',
        settings JSONB DEFAULT '{}',
        webhook_url TEXT,
        last_sync TIMESTAMP,
        error_message TEXT,
        created_at TIMESTAMP DEFAULT NOW(),
        updated_at TIMESTAMP DEFAULT NOW()
      )
    `;

    // Integration sync history table
    await sql`
      CREATE TABLE IF NOT EXISTS integration_sync_history (
        id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
        integration_id UUID REFERENCES integration_configs(id) ON DELETE CASCADE,
        sync_type VARCHAR(20) NOT NULL,
        status VARCHAR(20) NOT NULL,
        records_processed INTEGER DEFAULT 0,
        records_success INTEGER DEFAULT 0,
        records_failed INTEGER DEFAULT 0,
        error_details JSONB,
        started_at TIMESTAMP DEFAULT NOW(),
        completed_at TIMESTAMP,
        metadata JSONB DEFAULT '{}'
      )
    `;

    // Webhook events table
    await sql`
      CREATE TABLE IF NOT EXISTS webhook_events (
        id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
        integration_id UUID REFERENCES integration_configs(id) ON DELETE CASCADE,
        event_type VARCHAR(100) NOT NULL,
        payload JSONB NOT NULL,
        signature VARCHAR(255),
        processed BOOLEAN DEFAULT false,
        processed_at TIMESTAMP,
        error_message TEXT,
        retry_count INTEGER DEFAULT 0,
        created_at TIMESTAMP DEFAULT NOW()
      )
    `;

    // Integration data mapping table
    await sql`
      CREATE TABLE IF NOT EXISTS integration_data_mapping (
        id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
        integration_id UUID REFERENCES integration_configs(id) ON DELETE CASCADE,
        local_entity VARCHAR(100) NOT NULL,
        local_field VARCHAR(100) NOT NULL,
        external_entity VARCHAR(100) NOT NULL,
        external_field VARCHAR(100) NOT NULL,
        transformation_rule JSONB,
        is_bidirectional BOOLEAN DEFAULT false,
        created_at TIMESTAMP DEFAULT NOW()
      )
    `;

    // API rate limiting table
    await sql`
      CREATE TABLE IF NOT EXISTS api_rate_limits (
        id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
        integration_id UUID REFERENCES integration_configs(id) ON DELETE CASCADE,
        endpoint VARCHAR(255) NOT NULL,
        requests_made INTEGER DEFAULT 0,
        requests_limit INTEGER NOT NULL,
        window_start TIMESTAMP DEFAULT NOW(),
        window_duration_minutes INTEGER DEFAULT 60,
        last_request TIMESTAMP
      )
    `;

    // Create indexes
    await sql`CREATE INDEX IF NOT EXISTS idx_integration_configs_type ON integration_configs(type)`;
    await sql`CREATE INDEX IF NOT EXISTS idx_integration_configs_status ON integration_configs(status)`;
    await sql`CREATE INDEX IF NOT EXISTS idx_sync_history_integration ON integration_sync_history(integration_id)`;
    await sql`CREATE INDEX IF NOT EXISTS idx_sync_history_status ON integration_sync_history(status, started_at)`;
    await sql`CREATE INDEX IF NOT EXISTS idx_webhook_events_integration ON webhook_events(integration_id)`;
    await sql`CREATE INDEX IF NOT EXISTS idx_webhook_events_processed ON webhook_events(processed, created_at)`;
    await sql`CREATE INDEX IF NOT EXISTS idx_data_mapping_integration ON integration_data_mapping(integration_id)`;
    await sql`CREATE INDEX IF NOT EXISTS idx_rate_limits_integration ON api_rate_limits(integration_id, endpoint)`;

    console.log(' Third-party integration tables initialized successfully');
  } catch (error) {
    console.error(' Failed to initialize third-party integration tables:', error);
  }
}

/**
 * Integration Manager - Central hub for all integrations
 */
export class IntegrationManager {
  private static integrations = new Map<string, any>();

  /**
   * Register an integration
   */
  static registerIntegration(config: IntegrationConfig, implementation: any): void {
    this.integrations.set(config.id, {
      config,
      implementation,
      lastUsed: Date.now()
    });
  }

  /**
   * Get integration by ID
   */
  static getIntegration(integrationId: string): any {
    const integration = this.integrations.get(integrationId);
    if (integration) {
      integration.lastUsed = Date.now();
      return integration.implementation;
    }
    return null;
  }

  /**
   * Create new integration configuration
   */
  static async createIntegration(config: Omit<IntegrationConfig, 'id' | 'created_at' | 'updated_at'>): Promise<string> {
    try {
      if (!sql) {
        throw new Error('Database not available');
      }

      const result = await sql`
        INSERT INTO integration_configs (
          name, type, provider, status, credentials, settings, webhook_url
        )
        VALUES (
          ${config.name}, ${config.type}, ${config.provider}, ${config.status},
          ${JSON.stringify(config.credentials)}, ${JSON.stringify(config.settings)},
          ${config.webhook_url || null}
        )
        RETURNING id
      ` as Array<{ id: string }>;

      return result[0].id;
    } catch (error) {
      console.error('Failed to create integration:', error);
      throw error;
    }
  }

  /**
   * Update integration configuration
   */
  static async updateIntegration(
    integrationId: string, 
    updates: Partial<IntegrationConfig>
  ): Promise<void> {
    try {
      if (!sql) {
        throw new Error('Database not available');
      }

      const updateFields: string[] = [];
      const updateValues: any[] = [];

      Object.entries(updates).forEach(([key, value]) => {
        if (key !== 'id' && key !== 'created_at' && value !== undefined) {
          updateFields.push(`${key} = $${updateValues.length + 1}`);
          updateValues.push(typeof value === 'object' ? JSON.stringify(value) : value);
        }
      });

      if (updateFields.length > 0) {
        updateFields.push('updated_at = NOW()');

        await sql`
          UPDATE integration_configs 
          SET ${sql.unsafe(updateFields.join(', '))}
          WHERE id = ${integrationId}
        `;
      }
    } catch (error) {
      console.error('Failed to update integration:', error);
      throw error;
    }
  }

  /**
   * Get all integrations
   */
  static async getAllIntegrations(): Promise<IntegrationConfig[]> {
    try {
      if (!sql) {
        return [];
      }

      const integrations = await sql`
        SELECT * FROM integration_configs ORDER BY created_at DESC
      ` as IntegrationConfig[];

      return integrations;
    } catch (error) {
      console.error('Failed to get integrations:', error);
      return [];
    }
  }

  /**
   * Test integration connection
   */
  static async testIntegration(integrationId: string): Promise<{ success: boolean; error?: string }> {
    try {
      const integration = this.getIntegration(integrationId);
      if (!integration) {
        return { success: false, error: 'Integration not found' };
      }

      // Each integration should implement a test method
      if (typeof integration.test === 'function') {
        const result = await integration.test();

        // Update integration status based on test result
        await this.updateIntegration(integrationId, {
          status: result.success ? 'active' : 'error',
          error_message: result.error || null,
          last_sync: new Date().toISOString()
        });

        return result;
      }

      return { success: false, error: 'Test method not implemented' };
    } catch (error) {
      console.error('Integration test failed:', error);
      return { success: false, error: error instanceof Error ? error.message : 'Unknown error' };
    }
  }

  /**
   * Record sync result
   */
  static async recordSyncResult(result: SyncResult): Promise<void> {
    try {
      if (!sql) {
        console.warn(' Database not available, skipping sync result recording');
        return;
      }

      await sql`
        INSERT INTO integration_sync_history (
          integration_id, sync_type, status, records_processed, 
          records_success, records_failed, error_details, 
          started_at, completed_at
        )
        VALUES (
          ${result.integration_id}, ${result.sync_type}, ${result.status},
          ${result.records_processed}, ${result.records_success}, ${result.records_failed},
          ${result.error_details ? JSON.stringify(result.error_details) : null},
          ${result.started_at}, ${result.completed_at}
        )
      `;
    } catch (error) {
      console.error('Failed to record sync result:', error);
    }
  }

  /**
   * Process webhook event
   */
  static async processWebhookEvent(event: Omit<WebhookEvent, 'id' | 'created_at'>): Promise<void> {
    try {
      if (!sql) {
        console.warn(' Database not available, skipping webhook event processing');
        return;
      }

      // Store webhook event
      const result = await sql`
        INSERT INTO webhook_events (
          integration_id, event_type, payload, signature, retry_count
        )
        VALUES (
          ${event.integration_id}, ${event.event_type}, 
          ${JSON.stringify(event.payload)}, ${event.signature || null}, ${event.retry_count}
        )
        RETURNING id
      ` as Array<{ id: string }>;

      const webhookId = result[0].id;

      // Process the event
      const integration = this.getIntegration(event.integration_id);
      if (integration && typeof integration.processWebhook === 'function') {
        try {
          await integration.processWebhook(event.event_type, event.payload);

          // Mark as processed
          await sql`
            UPDATE webhook_events 
            SET processed = true, processed_at = NOW()
            WHERE id = ${webhookId}
          `;
        } catch (processingError) {
          // Mark as failed
          await sql`
            UPDATE webhook_events 
            SET error_message = ${processingError instanceof Error ? processingError.message : 'Processing failed'}
            WHERE id = ${webhookId}
          `;
        }
      }
    } catch (error) {
      console.error('Failed to process webhook event:', error);
    }
  }

  /**
   * Check API rate limits
   */
  static async checkRateLimit(
    integrationId: string, 
    endpoint: string, 
    limit: number, 
    windowMinutes: number = 60
  ): Promise<{ allowed: boolean; remaining: number; resetTime: Date }> {
    try {
      if (!sql) {
        return { allowed: true, remaining: limit, resetTime: new Date() };
      }

      const now = new Date();
      const windowStart = new Date(now.getTime() - windowMinutes * 60 * 1000);

      // Get current rate limit status
      const rateLimitData = await sql`
        SELECT * FROM api_rate_limits 
        WHERE integration_id = ${integrationId} 
        AND endpoint = ${endpoint}
        AND window_start > ${windowStart.toISOString()}
      ` as Array<{
        requests_made: number;
        requests_limit: number;
        window_start: string;
        window_duration_minutes: number;
      }>;

      let requestsMade = 0;
      let resetTime = new Date(now.getTime() + windowMinutes * 60 * 1000);

      if (rateLimitData.length > 0) {
        const data = rateLimitData[0];
        requestsMade = data.requests_made;
        resetTime = new Date(new Date(data.window_start).getTime() + data.window_duration_minutes * 60 * 1000);
      }

      const allowed = requestsMade < limit;
      const remaining = Math.max(0, limit - requestsMade);

      if (allowed) {
        // Increment request count
        await sql`
          INSERT INTO api_rate_limits (
            integration_id, endpoint, requests_made, requests_limit, 
            window_duration_minutes, last_request
          )
          VALUES (
            ${integrationId}, ${endpoint}, 1, ${limit}, ${windowMinutes}, NOW()
          )
          ON CONFLICT (integration_id, endpoint) DO UPDATE SET
            requests_made = CASE 
              WHEN api_rate_limits.window_start < ${windowStart.toISOString()} THEN 1
              ELSE api_rate_limits.requests_made + 1
            END,
            window_start = CASE
              WHEN api_rate_limits.window_start < ${windowStart.toISOString()} THEN NOW()
              ELSE api_rate_limits.window_start
            END,
            last_request = NOW()
        `;
      }

      return { allowed, remaining, resetTime };
    } catch (error) {
      console.error('Failed to check rate limit:', error);
      return { allowed: true, remaining: limit, resetTime: new Date() };
    }
  }
}

/**
 * HubSpot CRM Integration
 */
export class HubSpotIntegration implements CRMIntegration {
  private apiKey: string;
  private baseUrl = 'https://api.hubapi.com';

  constructor(apiKey: string) {
    this.apiKey = apiKey;
  }

  async test(): Promise<{ success: boolean; error?: string }> {
    try {
      const response = await fetch(`${this.baseUrl}/contacts/v1/lists/all/contacts/all?count=1&hapikey=${this.apiKey}`);
      return { success: response.ok };
    } catch (error) {
      return { success: false, error: error instanceof Error ? error.message : 'Connection failed' };
    }
  }

  async syncCustomers(): Promise<SyncResult> {
    const startTime = new Date().toISOString();
    let recordsProcessed = 0;
    let recordsSuccess = 0;
    let recordsFailed = 0;
    const errorDetails: string[] = [];

    try {
      // Get customers from local database
      if (sql) {
        const customers = await sql`
          SELECT id, email, first_name, last_name, phone, created_at
          FROM users 
          WHERE email IS NOT NULL
          LIMIT 100
        ` as Array<{
          id: string;
          email: string;
          first_name: string;
          last_name: string;
          phone: string;
          created_at: string;
        }>;

        for (const customer of customers) {
          recordsProcessed++;

          try {
            const result = await this.createContact({
              email: customer.email,
              firstname: customer.first_name,
              lastname: customer.last_name,
              phone: customer.phone,
              createdate: customer.created_at
            });

            if (result.success) {
              recordsSuccess++;
            } else {
              recordsFailed++;
              if (result.error) errorDetails.push(result.error);
            }
          } catch (error) {
            recordsFailed++;
            errorDetails.push(`Customer ${customer.email}: ${error instanceof Error ? error.message : 'Unknown error'}`);
          }
        }
      }

      return {
        integration_id: 'hubspot',
        sync_type: 'full',
        status: recordsFailed === 0 ? 'success' : (recordsSuccess > 0 ? 'partial' : 'failed'),
        records_processed: recordsProcessed,
        records_success: recordsSuccess,
        records_failed: recordsFailed,
        error_details: errorDetails.length > 0 ? errorDetails : undefined,
        started_at: startTime,
        completed_at: new Date().toISOString()
      };
    } catch (error) {
      return {
        integration_id: 'hubspot',
        sync_type: 'full',
        status: 'failed',
        records_processed: recordsProcessed,
        records_success: recordsSuccess,
        records_failed: recordsFailed,
        error_details: [error instanceof Error ? error.message : 'Sync failed'],
        started_at: startTime,
        completed_at: new Date().toISOString()
      };
    }
  }

  async syncOrders(): Promise<SyncResult> {
    // Similar implementation for orders
    return {
      integration_id: 'hubspot',
      sync_type: 'full',
      status: 'success',
      records_processed: 0,
      records_success: 0,
      records_failed: 0,
      started_at: new Date().toISOString(),
      completed_at: new Date().toISOString()
    };
  }

  async createContact(customerData: any): Promise<{ success: boolean; id?: string; error?: string }> {
    try {
      const response = await fetch(`${this.baseUrl}/contacts/v1/contact?hapikey=${this.apiKey}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ properties: customerData })
      });

      if (response.ok) {
        const result = await response.json();
        return { success: true, id: result.vid?.toString() };
      } else {
        const error = await response.text();
        return { success: false, error };
      }
    } catch (error) {
      return { success: false, error: error instanceof Error ? error.message : 'Unknown error' };
    }
  }

  async updateContact(contactId: string, data: any): Promise<{ success: boolean; error?: string }> {
    try {
      const response = await fetch(`${this.baseUrl}/contacts/v1/contact/vid/${contactId}/profile?hapikey=${this.apiKey}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ properties: data })
      });

      return { success: response.ok, error: response.ok ? undefined : await response.text() };
    } catch (error) {
      return { success: false, error: error instanceof Error ? error.message : 'Unknown error' };
    }
  }

  async createDeal(dealData: any): Promise<{ success: boolean; id?: string; error?: string }> {
    try {
      const response = await fetch(`${this.baseUrl}/deals/v1/deal?hapikey=${this.apiKey}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ properties: dealData })
      });

      if (response.ok) {
        const result = await response.json();
        return { success: true, id: result.dealId?.toString() };
      } else {
        const error = await response.text();
        return { success: false, error };
      }
    } catch (error) {
      return { success: false, error: error instanceof Error ? error.message : 'Unknown error' };
    }
  }

  async getContacts(_filters?: any): Promise<any[]> {
    try {
      const response = await fetch(`${this.baseUrl}/contacts/v1/lists/all/contacts/all?hapikey=${this.apiKey}`);
      if (response.ok) {
        const result = await response.json();
        return result.contacts || [];
      }
      return [];
    } catch (error) {
      console.error('Failed to get contacts:', error);
      return [];
    }
  }
}

/**
 * Twilio SMS Integration
 */
export class TwilioSMSIntegration implements SMSIntegration {
  private accountSid: string;
  private authToken: string;
  private fromNumber: string;

  constructor(accountSid: string, authToken: string, fromNumber: string) {
    this.accountSid = accountSid;
    this.authToken = authToken;
    this.fromNumber = fromNumber;
  }

  async test(): Promise<{ success: boolean; error?: string }> {
    try {
      // Test by validating credentials with Twilio API
      const auth = Buffer.from(`${this.accountSid}:${this.authToken}`).toString('base64');
      const response = await fetch(`https://api.twilio.com/2010-04-01/Accounts/${this.accountSid}.json`, {
        headers: { 'Authorization': `Basic ${auth}` }
      });

      return { success: response.ok };
    } catch (error) {
      return { success: false, error: error instanceof Error ? error.message : 'Connection failed' };
    }
  }

  async sendSMS(to: string, message: string, _metadata?: any): Promise<{ success: boolean; messageId?: string; error?: string }> {
    try {
      const auth = Buffer.from(`${this.accountSid}:${this.authToken}`).toString('base64');

      const response = await fetch(`https://api.twilio.com/2010-04-01/Accounts/${this.accountSid}/Messages.json`, {
        method: 'POST',
        headers: {
          'Authorization': `Basic ${auth}`,
          'Content-Type': 'application/x-www-form-urlencoded'
        },
        body: new URLSearchParams({
          From: this.fromNumber,
          To: to,
          Body: message
        })
      });

      if (response.ok) {
        const result = await response.json();
        return { success: true, messageId: result.sid };
      } else {
        const error = await response.text();
        return { success: false, error };
      }
    } catch (error) {
      return { success: false, error: error instanceof Error ? error.message : 'Unknown error' };
    }
  }

  async sendBulkSMS(recipients: Array<{ phone: string; message: string }>): Promise<SyncResult> {
    const startTime = new Date().toISOString();
    let recordsProcessed = 0;
    let recordsSuccess = 0;
    let recordsFailed = 0;
    const errorDetails: string[] = [];

    for (const recipient of recipients) {
      recordsProcessed++;

      try {
        const result = await this.sendSMS(recipient.phone, recipient.message);
        if (result.success) {
          recordsSuccess++;
        } else {
          recordsFailed++;
          if (result.error) errorDetails.push(`${recipient.phone}: ${result.error}`);
        }
      } catch (error) {
        recordsFailed++;
        errorDetails.push(`${recipient.phone}: ${error instanceof Error ? error.message : 'Unknown error'}`);
      }
    }

    return {
      integration_id: 'twilio',
      sync_type: 'full',
      status: recordsFailed === 0 ? 'success' : (recordsSuccess > 0 ? 'partial' : 'failed'),
      records_processed: recordsProcessed,
      records_success: recordsSuccess,
      records_failed: recordsFailed,
      error_details: errorDetails.length > 0 ? errorDetails : undefined,
      started_at: startTime,
      completed_at: new Date().toISOString()
    };
  }

  async getDeliveryStatus(messageId: string): Promise<{ status: string; delivered_at?: string }> {
    try {
      const auth = Buffer.from(`${this.accountSid}:${this.authToken}`).toString('base64');

      const response = await fetch(`https://api.twilio.com/2010-04-01/Accounts/${this.accountSid}/Messages/${messageId}.json`, {
        headers: { 'Authorization': `Basic ${auth}` }
      });

      if (response.ok) {
        const result = await response.json();
        return {
          status: result.status,
          delivered_at: result.date_sent
        };
      }

      return { status: 'unknown' };
    } catch (error) {
      console.error('Failed to get delivery status:', error);
      return { status: 'error' };
    }
  }

  async validatePhoneNumber(phone: string): Promise<{ valid: boolean; formatted?: string }> {
    try {
      const auth = Buffer.from(`${this.accountSid}:${this.authToken}`).toString('base64');

      const response = await fetch(`https://lookups.twilio.com/v1/PhoneNumbers/${encodeURIComponent(phone)}`, {
        headers: { 'Authorization': `Basic ${auth}` }
      });

      if (response.ok) {
        const result = await response.json();
        return {
          valid: true,
          formatted: result.phone_number
        };
      }

      return { valid: false };
    } catch (error) {
      console.error('Failed to validate phone number:', error);
      return { valid: false };
    }
  }
}

/**
 * Run integration health checks
 */
export async function runIntegrationHealthChecks(): Promise<void> {
  try {
    console.log(' Running integration health checks...');

    const integrations = await IntegrationManager.getAllIntegrations();

    for (const integration of integrations) {
      if (integration.status === 'active') {
        console.log(`Testing ${integration.name} (${integration.provider})...`);

        const testResult = await IntegrationManager.testIntegration(integration.id);

        if (testResult.success) {
          console.log(` ${integration.name} is healthy`);
        } else {
          console.log(` ${integration.name} failed: ${testResult.error}`);
        }
      }
    }

    // Process pending webhook events
    if (sql) {
      const pendingWebhooks = await sql`
        SELECT * FROM webhook_events 
        WHERE processed = false 
        AND retry_count < 3
        ORDER BY created_at ASC
        LIMIT 50
      ` as WebhookEvent[];

      for (const webhook of pendingWebhooks) {
        try {
          await IntegrationManager.processWebhookEvent({
            integration_id: webhook.integration_id,
            event_type: webhook.event_type,
            payload: webhook.payload,
            signature: webhook.signature,
            processed: false,
            retry_count: webhook.retry_count + 1
          });
        } catch (error) {
          console.error(`Failed to process webhook ${webhook.id}:`, error);
        }
      }
    }

    console.log(' Integration health checks completed');
  } catch (error) {
    console.error(' Integration health checks failed:', error);
  }
}

// Initialize third-party integration tables on module load
initializeThirdPartyIntegrationTables();

// Run integration health checks every 30 minutes
setInterval(runIntegrationHealthChecks, 30 * 60 * 1000);

import { sql } from '../lib/neon';
import { 
  hashPassword, 
  verifyPassword, 
  generateToken, 
  generateRefreshToken, 
  generateVerificationToken,
  verifyToken,
  isValidEmail,
  validatePassword,
  checkRateLimit,
  logAuthEvent,
  initializeAuthTables,
  type User,
  type AuthResult
} from '../lib/auth';
import { emailService } from './emailService';

// Initialize auth tables on service load
initializeAuthTables();

export const authService = {
  async login(email: string, password: string, ipAddress: string = '', userAgent: string = ''): Promise<AuthResult> {
    try {
      // Rate limiting
      if (!checkRateLimit(`login_${email}`, 5, 15 * 60 * 1000)) {
        await logAuthEvent(null, 'login_failed', ipAddress, userAgent, { reason: 'rate_limited', email });
        return { success: false, error: 'Too many login attempts. Please try again in 15 minutes.' };
      }

      // Validate email format
      if (!isValidEmail(email)) {
        return { success: false, error: 'Invalid email format' };
      }

      if (!sql) {
        console.warn(' Database not available');
        return { success: false, error: 'Service temporarily unavailable' };
      }

      // Get user from database
      const users = await sql`
        SELECT id, email, password_hash, first_name, last_name, phone, role, email_verified, 
               failed_login_attempts, locked_until, created_at, updated_at
        FROM users 
        WHERE email = ${email.toLowerCase()}
      ` as Array<User & { password_hash: string; failed_login_attempts: number; locked_until: string | null }>;

      if (users.length === 0) {
        await logAuthEvent(null, 'login_failed', ipAddress, userAgent, { reason: 'user_not_found', email });
        return { success: false, error: 'Invalid email or password' };
      }

      const user = users[0];

      // Check if account is locked
      if (user.locked_until && new Date(user.locked_until) > new Date()) {
        await logAuthEvent(user.id, 'login_failed', ipAddress, userAgent, { reason: 'account_locked' });
        return { success: false, error: 'Account is temporarily locked. Please try again later.' };
      }

      // Verify password
      const isValidPassword = await verifyPassword(password, user.password_hash);

      if (!isValidPassword) {
        // Increment failed attempts
        const newFailedAttempts = (user.failed_login_attempts || 0) + 1;
        const lockUntil = newFailedAttempts >= 5 ? new Date(Date.now() + 30 * 60 * 1000) : null; // Lock for 30 minutes after 5 failed attempts

        await sql`
          UPDATE users 
          SET failed_login_attempts = ${newFailedAttempts}, 
              locked_until = ${lockUntil?.toISOString() || null}
          WHERE id = ${user.id}
        `;

        await logAuthEvent(user.id, 'login_failed', ipAddress, userAgent, { 
          reason: 'invalid_password', 
          failed_attempts: newFailedAttempts 
        });

        return { success: false, error: 'Invalid email or password' };
      }

      // Check if email is verified
      if (!user.email_verified) {
        return { success: false, error: 'Please verify your email address before logging in' };
      }

      // Reset failed attempts on successful login
      await sql`
        UPDATE users 
        SET failed_login_attempts = 0, locked_until = NULL, last_login = NOW()
        WHERE id = ${user.id}
      `;

      // Generate tokens
      const token = generateToken(user);
      const refreshToken = generateRefreshToken(user.id);

      // Store refresh token in database
      await sql`
        INSERT INTO refresh_tokens (user_id, token, expires_at)
        VALUES (${user.id}, ${refreshToken}, ${new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString()})
      `;

      // Log successful login
      await logAuthEvent(user.id, 'login_success', ipAddress, userAgent);

      // Remove sensitive data
      const { password_hash, failed_login_attempts, locked_until, ...safeUser } = user;

      return { 
        success: true, 
        user: safeUser,
        token,
        refreshToken
      };
    } catch (error) {
      console.error('Login error:', error);
      return { success: false, error: 'Login failed. Please try again.' };
    }
  },

  async register(
    email: string, 
    password: string, 
    firstName: string,
    lastName: string,
    phone?: string,
    ipAddress: string = '',
    userAgent: string = ''
  ): Promise<AuthResult> {
    try {
      // Rate limiting for registration
      if (!checkRateLimit(`register_${ipAddress}`, 3, 60 * 60 * 1000)) {
        return { success: false, error: 'Too many registration attempts. Please try again in 1 hour.' };
      }

      // Validate input
      if (!isValidEmail(email)) {
        return { success: false, error: 'Invalid email format' };
      }

      const passwordValidation = validatePassword(password);
      if (!passwordValidation.isValid) {
        return { success: false, error: passwordValidation.errors.join(', ') };
      }

      if (!firstName.trim() || !lastName.trim()) {
        return { success: false, error: 'First name and last name are required' };
      }

      if (!sql) {
        console.warn(' Database not available');
        return { success: false, error: 'Service temporarily unavailable' };
      }

      // Check if user already exists
      const existingUsers = await sql`
        SELECT id FROM users WHERE email = ${email.toLowerCase()}
      `;

      if (existingUsers.length > 0) {
        return { success: false, error: 'An account with this email already exists' };
      }

      // Hash password
      const passwordHash = await hashPassword(password);

      // Generate verification token
      const verificationToken = generateVerificationToken();

      // Create user
      const users = await sql`
        INSERT INTO users (email, password_hash, first_name, last_name, phone, role, email_verified)
        VALUES (${email.toLowerCase()}, ${passwordHash}, ${firstName.trim()}, ${lastName.trim()}, ${phone || null}, 'customer', false)
        RETURNING id, email, first_name, last_name, phone, role, email_verified, created_at, updated_at
      ` as Array<User>;

      if (users.length === 0) {
        return { success: false, error: 'Failed to create account' };
      }

      const user = users[0];

      // Store verification token
      await sql`
        INSERT INTO email_verification_tokens (user_id, token, expires_at)
        VALUES (${user.id}, ${verificationToken}, ${new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString()})
      `;

      // Send verification email
      try {
        await emailService.sendVerificationEmail(user.email, user.first_name || 'User', verificationToken);
      } catch (emailError) {
        console.error('Failed to send verification email:', emailError);
        // Don't fail registration if email fails
      }

      // Log registration
      await logAuthEvent(user.id, 'login_success', ipAddress, userAgent, { event: 'registration' });

      return { 
        success: true, 
        user,
        error: 'Account created successfully. Please check your email to verify your account.'
      };
    } catch (error) {
      console.error('Registration error:', error);
      return { success: false, error: 'Registration failed. Please try again.' };
    }
  },

  async logout(token: string, ipAddress: string = '', userAgent: string = ''): Promise<{ success: boolean }> {
    try {
      const decoded = verifyToken(token);
      if (decoded) {
        // Revoke refresh token
        if (sql) {
          await sql`
            UPDATE refresh_tokens 
            SET revoked_at = NOW() 
            WHERE user_id = ${decoded.userId} AND revoked_at IS NULL
          `;
        }

        // Log logout
        await logAuthEvent(decoded.userId, 'logout', ipAddress, userAgent);
      }

      return { success: true };
    } catch (error) {
      console.error('Logout error:', error);
      return { success: true }; // Always return success for logout
    }
  },

  async getCurrentUser(token: string): Promise<User | null> {
    try {
      const decoded = verifyToken(token);
      if (!decoded) {
        return null;
      }

      if (!sql) {
        console.warn(' Database not available');
        return null;
      }

      const users = await sql`
        SELECT id, email, first_name, last_name, phone, role, email_verified, created_at, updated_at
        FROM users 
        WHERE id = ${decoded.userId}
      ` as Array<User>;

      return users.length > 0 ? users[0] : null;
    } catch (error) {
      console.error('Get current user error:', error);
      return null;
    }
  },

  async refreshToken(refreshToken: string): Promise<AuthResult> {
    try {
      if (!sql) {
        return { success: false, error: 'Service temporarily unavailable' };
      }

      // Verify refresh token
      const tokenRecords = await sql`
        SELECT rt.user_id, rt.expires_at, u.email, u.first_name, u.last_name, u.phone, u.role, u.email_verified, u.created_at, u.updated_at
        FROM refresh_tokens rt
        JOIN users u ON rt.user_id = u.id
        WHERE rt.token = ${refreshToken} AND rt.revoked_at IS NULL
      ` as Array<{ user_id: string; expires_at: string } & User>;

      if (tokenRecords.length === 0) {
        return { success: false, error: 'Invalid refresh token' };
      }

      const record = tokenRecords[0];

      // Check if token is expired
      if (new Date(record.expires_at) < new Date()) {
        return { success: false, error: 'Refresh token expired' };
      }

      // Generate new tokens
      const user: User = {
        id: record.user_id,
        email: record.email,
        first_name: record.first_name,
        last_name: record.last_name,
        phone: record.phone,
        role: record.role,
        email_verified: record.email_verified,
        created_at: record.created_at,
        updated_at: record.updated_at
      };

      const newToken = generateToken(user);
      const newRefreshToken = generateRefreshToken(user.id);

      // Revoke old refresh token and create new one
      await sql`
        UPDATE refresh_tokens 
        SET revoked_at = NOW() 
        WHERE token = ${refreshToken}
      `;

      await sql`
        INSERT INTO refresh_tokens (user_id, token, expires_at)
        VALUES (${user.id}, ${newRefreshToken}, ${new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString()})
      `;

      return {
        success: true,
        user,
        token: newToken,
        refreshToken: newRefreshToken
      };
    } catch (error) {
      console.error('Refresh token error:', error);
      return { success: false, error: 'Failed to refresh token' };
    }
  },

  async verifyEmail(token: string): Promise<{ success: boolean; error?: string }> {
    try {
      if (!sql) {
        return { success: false, error: 'Service temporarily unavailable' };
      }

      // Find verification token
      const tokenRecords = await sql`
        SELECT user_id, expires_at, used_at
        FROM email_verification_tokens
        WHERE token = ${token}
      ` as Array<{ user_id: string; expires_at: string; used_at: string | null }>;

      if (tokenRecords.length === 0) {
        return { success: false, error: 'Invalid verification token' };
      }

      const record = tokenRecords[0];

      // Check if token is already used
      if (record.used_at) {
        return { success: false, error: 'Verification token already used' };
      }

      // Check if token is expired
      if (new Date(record.expires_at) < new Date()) {
        return { success: false, error: 'Verification token expired' };
      }

      // Mark user as verified and token as used
      await sql`
        UPDATE users 
        SET email_verified = true 
        WHERE id = ${record.user_id}
      `;

      await sql`
        UPDATE email_verification_tokens 
        SET used_at = NOW() 
        WHERE token = ${token}
      `;

      // Log email verification
      await logAuthEvent(record.user_id, 'email_verified', '', '');

      return { success: true };
    } catch (error) {
      console.error('Email verification error:', error);
      return { success: false, error: 'Email verification failed' };
    }
  },

  async requestPasswordReset(email: string, ipAddress: string = '', userAgent: string = ''): Promise<{ success: boolean; error?: string }> {
    try {
      // Rate limiting
      if (!checkRateLimit(`password_reset_${email}`, 3, 60 * 60 * 1000)) {
        return { success: false, error: 'Too many password reset attempts. Please try again in 1 hour.' };
      }

      if (!isValidEmail(email)) {
        return { success: false, error: 'Invalid email format' };
      }

      if (!sql) {
        return { success: false, error: 'Service temporarily unavailable' };
      }

      // Check if user exists
      const users = await sql`
        SELECT id, first_name, email_verified
        FROM users 
        WHERE email = ${email.toLowerCase()}
      ` as Array<{ id: string; first_name: string; email_verified: boolean }>;

      // Always return success to prevent email enumeration
      if (users.length === 0) {
        return { success: true };
      }

      const user = users[0];

      // Only send reset email if account is verified
      if (!user.email_verified) {
        return { success: true };
      }

      // Generate reset token
      const resetToken = generateVerificationToken();

      // Store reset token
      await sql`
        INSERT INTO password_reset_tokens (user_id, token, expires_at)
        VALUES (${user.id}, ${resetToken}, ${new Date(Date.now() + 60 * 60 * 1000).toISOString()})
      `;

      // Send reset email
      try {
        const resetUrl = `${import.meta.env.VITE_APP_URL || 'https://rise-via.vercel.app'}/reset-password?token=${resetToken}`;

        await emailService.sendEmail(
          email,
          'Password Reset Request',
          `
            <h1>Password Reset Request</h1>
            <p>Hi ${user.first_name},</p>
            <p>You requested a password reset for your Rise Via account.</p>
            <p>Click the link below to reset your password:</p>
            <a href="${resetUrl}" style="background: #7c3aed; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; display: inline-block;">
              Reset Password
            </a>
            <p>This link will expire in 1 hour.</p>
            <p>If you didn't request this reset, you can safely ignore this email.</p>
            <p>The Rise Via Team</p>
          `
        );
      } catch (emailError) {
        console.error('Failed to send password reset email:', emailError);
      }

      // Log password reset request
      await logAuthEvent(user.id, 'password_reset', ipAddress, userAgent, { event: 'request' });

      return { success: true };
    } catch (error) {
      console.error('Password reset request error:', error);
      return { success: false, error: 'Password reset request failed' };
    }
  },

  async resetPassword(token: string, newPassword: string, ipAddress: string = '', userAgent: string = ''): Promise<{ success: boolean; error?: string }> {
    try {
      // Validate password
      const passwordValidation = validatePassword(newPassword);
      if (!passwordValidation.isValid) {
        return { success: false, error: passwordValidation.errors.join(', ') };
      }

      if (!sql) {
        return { success: false, error: 'Service temporarily unavailable' };
      }

      // Find reset token
      const tokenRecords = await sql`
        SELECT user_id, expires_at, used_at
        FROM password_reset_tokens
        WHERE token = ${token}
      ` as Array<{ user_id: string; expires_at: string; used_at: string | null }>;

      if (tokenRecords.length === 0) {
        return { success: false, error: 'Invalid reset token' };
      }

      const record = tokenRecords[0];

      // Check if token is already used
      if (record.used_at) {
        return { success: false, error: 'Reset token already used' };
      }

      // Check if token is expired
      if (new Date(record.expires_at) < new Date()) {
        return { success: false, error: 'Reset token expired' };
      }

      // Hash new password
      const passwordHash = await hashPassword(newPassword);

      // Update password and mark token as used
      await sql`
        UPDATE users 
        SET password_hash = ${passwordHash}, failed_login_attempts = 0, locked_until = NULL
        WHERE id = ${record.user_id}
      `;

      await sql`
        UPDATE password_reset_tokens 
        SET used_at = NOW() 
        WHERE token = ${token}
      `;

      // Revoke all refresh tokens for this user
      await sql`
        UPDATE refresh_tokens 
        SET revoked_at = NOW() 
        WHERE user_id = ${record.user_id} AND revoked_at IS NULL
      `;

      // Log password reset
      await logAuthEvent(record.user_id, 'password_reset', ipAddress, userAgent, { event: 'completed' });

      return { success: true };
    } catch (error) {
      console.error('Password reset error:', error);
      return { success: false, error: 'Password reset failed' };
    }
  },

  async resendVerificationEmail(email: string): Promise<{ success: boolean; error?: string }> {
    try {
      if (!isValidEmail(email)) {
        return { success: false, error: 'Invalid email format' };
      }

      if (!sql) {
        return { success: false, error: 'Service temporarily unavailable' };
      }

      // Check if user exists and is not verified
      const users = await sql`
        SELECT id, first_name, email_verified
        FROM users 
        WHERE email = ${email.toLowerCase()}
      ` as Array<{ id: string; first_name: string; email_verified: boolean }>;

      if (users.length === 0) {
        return { success: false, error: 'User not found' };
      }

      const user = users[0];

      if (user.email_verified) {
        return { success: false, error: 'Email is already verified' };
      }

      // Generate new verification token
      const verificationToken = generateVerificationToken();

      // Store verification token
      await sql`
        INSERT INTO email_verification_tokens (user_id, token, expires_at)
        VALUES (${user.id}, ${verificationToken}, ${new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString()})
      `;

      // Send verification email
      await emailService.sendVerificationEmail(email, user.first_name || 'User', verificationToken);

      return { success: true };
    } catch (error) {
      console.error('Resend verification email error:', error);
      return { success: false, error: 'Failed to resend verification email' };
    }
  }
};
